var write=document.getElementById("writeUs"),modal=document.querySelector(".modal-bg"),close=document.getElementById("close"),customer=modal.querySelector("[name=customer]");write.addEventListener("click",function(e){e.preventDefault(),modal.classList.add("modal-show"),customer.focus()}),close.addEventListener("click",function(e){e.preventDefault(),modal.classList.remove("modal-show")}),window.onclick=function(e){e.target==modal&&modal.classList.remove("modal-show")};var mapBtn=document.querySelector(".map-img"),map=document.getElementById("map");mapBtn.addEventListener("click",function(e){e.preventDefault(),map.classList.add("modal-show")}),window.onclick=function(e){e.target==map&&map.classList.remove("modal-show")};